query($id: String!) {
    artist(id: $id) {
        id
        firstName
        lastName
        username
        email
        bands {
            username
        }
        following {
            username
        }

        followers {
            username
        }
    }
}

mutation(
    $email: String!, 
    $password: String!, 
    $username: String!, 
    $firstName: String!, 
    $lastName: String!
    ) {
    register(
        options: 
            {
                email: $email, 
                password: $password, 
                username: $username, 
                firstName: $firstName, 
                lastName: $lastName
            }
    ) {
        artist {
            id
            firstName
            lastName
            username
            email
       }
       errors {
           field
           message
       }
    }
}

mutation($usernameOrEmail: String!, $password: String!) {
    login(usernameOrEmail: $usernameOrEmail, password: $password) {
        errors {
            message
        }
        artist {
            id
            firstName
            lastName
            username
            email
            bands {
                id
                username
                members {
                    email
                }
            }
            following {
                username
            }
            followers {
                username
            }
       }
    }
}

