mutation($usernameOrEmail: String!, $password: String!) {
    login(usernameOrEmail: $usernameOrEmail, password: $password) {
        user {
            id
            firstName
            lastName
            username
            email
            bands {
                id
                name
                users {
                    email
                }
            }
       }
    }
}

mutation(
    $email: String!, 
    $password: String!, 
    $username: String!, 
    $firstName: String!, 
    $lastName: String!
    ) {
    register(
        options: 
            {
                email: $email, 
                password: $password, 
                username: $username, 
                firstName: $firstName, 
                lastName: $lastName
            }
    ) {
        user {
            id
            firstName
            lastName
            username
            email
       }
       errors {
           field
           message
       }
    }
}

mutation($bandId: String!, $userId: String!) {
    addUserToBand(bandId: $bandId, userId: $userId) {
            id
            username
            email
            bands {
                id
                name
            }

    }
}

query($id: String!) {
    band(id: $id) {
        id
        name
        users {
            username
        }
    }
}
